pkg load statistics;err = 0.0005;alpha = 0.05;num_simul = ceil(0.25*(norminv(alpha, 0, 1) / err)^2)p = 0.5;A = simulate_geo(num_simul, p);fprintf("Simulated prob. P(A=2) := %1.5f\n", mean(A == 2))fprintf("Ground truth    P(A=2) := %1.5f\n", geopdf(2, p))fprintf("Error                  := %e\n\n", abs(mean(A == 2) - geopdf(2, p)))fprintf("Simulated prob. P(A<=2) := %1.5f\n", mean(A <= 2))fprintf("Ground truth    P(A<=2) := %1.5f\n", geocdf(2, p))fprintf("Error                   := %e\n\n", abs(mean(A <= 2) - geocdf(2, p)))fprintf("Simulated prob. P(A<2) := %1.5f\n", mean(A < 2))fprintf("Ground truth    P(A<2) := %1.5f\n", geocdf(1, p))fprintf("Error                  := %e\n\n", abs(mean(A < 2) - geocdf(1, p)))fprintf("Simulated mean  E(A) := %1.5f\n", mean(A))fprintf("Ground truth    E(A) := %1.5f\n", (1-p)/p)fprintf("Error                := %e\n\n", abs(mean(A) - (1-p)/p))