pkg load statistics;## Given the polinomial: f(x) = 3/2*x^3 + 11/8*x^2 + 1/6*x + 1/12, 0 <= x <= 1## Use the rejection method to extract a sample out of this PDF distribution.f = [3/2, 11/8, 1/6, 1/12]t = linspace(0, 1);plot(t, polyval(f, t));hold on;plot(t, unifpdf(t));## c is maximum value the polynomial takes; aka. the highest peak the proposal ## distribution must cover, so g = c * U(0,1).c = polyval(f, 1)plot(t, c * unifpdf(t));hold off;num_trials = 4500;sample_size = 100;sample_pos = 1;X = rand(num_trials, 1);U = rand(num_trials, 1);i = 1;while i <= num_trials && sample_pos <= sample_size  test_u = U(i);  test_x = polyval(f, X(i)) / (c * unifpdf(X(i)));  if test_u <= test_x    accepted(sample_pos) = X(i);    sample_pos = sample_pos + 1;
  endif
  i = i  + 1;endwhilefprintf("# %d out of %d trials:\nneeded to obtain a sample of %d values from f(x) PDF.\n", i, num_trials, length(accepted))accepted;