pkg load statistics;err = 0.05;alpha = 0.05;num_simul = ceil(0.25*(norminv(alpha/2)/err)^2)lambda = 3.2for i = 1 : num_simul  A(i) = poisson_special_method(lambda);endfor fprintf("Simulated prob. P(A=2) := %1.5f\n", mean(A == 2))fprintf("Ground truth    P(A=2) := %1.5f\n", poisspdf(2, lambda))fprintf("Error                  := %e\n\n", abs(mean(A == 2) - poisspdf(2, lambda)))fprintf("Simulated prob. P(A<=2) := %1.5f\n", mean(A <= 2))fprintf("Ground truth    P(A<=2) := %1.5f\n", poisscdf(2, lambda))fprintf("Error                   := %e\n\n", abs(mean(A <= 2) - poisscdf(2, lambda)))fprintf("Simulated prob. P(A<2) := %1.5f\n", mean(A < 2))fprintf("Ground truth    P(A<2) := %1.5f\n", poisscdf(1, lambda))fprintf("Error                  := %e\n\n", abs(mean(A < 2) - poisscdf(1, lambda)))fprintf("Simulated mean  E(A) := %1.5f\n", mean(A))fprintf("Ground truth    E(A) := %1.5f\n", lambda)fprintf("Error                := %e\n\n", abs(mean(A) - lambda))