function Hx = hermiteInterpolation(nodes, values, derivatives, x)  table = divDifDouble(nodes, values, derivatives);  coefs = table(1,:);  doubleNodes = repelem(nodes, 2);  Hx = coefs(1);  P = 1;  for k = 2 : length(coefs)    P = P * (x - doubleNodes(k-1));    Hx = Hx + coefs(k) * P;  endendfunction##>> nodes=[0 3 5 8 13];##>> values=[0 255 383 623 993];##>> derivatives=[75 77 80 74 72];##>> Hx = hermiteInterpolation(nodes, values, derivatives, 10)##>> nodes=[1 2];##>> values=[0 0.6931];##>> derivatives=[1 0.5];##>> Hx = hermiteInterpolation(nodes, values, derivatives, 1.5)