function hermitePlot(nodes, values, derivatives)  otherNodes = linspace(-5, 5, 100);  n = length(otherNodes);  hermite = zeros(n, 1);  for i=1:n    hermite(i) = hermiteInterpolation(nodes, values, derivatives, otherNodes(i));  endfor  clf; hold on;  plot(nodes, values);  plot(otherNodes, hermite);
endfunction##nodes=linspace(-5,5,15);##values=arrayfun(@(x) sin(2*x), nodes);##derivatives=arrayfun(@(x) 2.*cos(2*x), nodes);##hermitePlot(nodes, values, derivatives)##>> nodes=linspace(1,5,15);##>> values=arrayfun(@(x) log(x), nodes);##>> derivatives=arrayfun(@(x) 1/x, nodes);##>> hermitePlot(nodes, values, derivatives)